<?php/*Template Name: All Comments*/?><?php get_header(); ?><script>$("#menu-item-32").addClass('current-menu-item');</script><div class="contentCol">  <div class="pageItem"><div class="text">    	<div class="text_comments"><?php define('DEFAULT_COMMENTS_PER_PAGE',15);$urlsplit = explode("/",$_SERVER["REQUEST_URI"]);if($urlsplit[2] == ""){	$page = 1;}else {	$page = (int) $urlsplit[2];}$limit = DEFAULT_COMMENTS_PER_PAGE;$offset = ($page * $limit) - $limit;$param = array(    'status'=>'approve',    'offset'=>$offset,    'number'=>$limit,);$total_comments = get_comments(array('status'=>'approve'));$pages = ceil(count($total_comments)/DEFAULT_COMMENTS_PER_PAGE);$comments = get_comments($param);foreach($comments as $comment) {	?>	<div class="one_commnents">	        <div class="name_user_comment">	                <?php echo $comment->comment_author; ?> - <a href="<?php echo get_permalink($comment->comment_post_ID); ?>"><?php echo get_the_title($comment->comment_post_ID);?></a>	                <div class="time_of_birth"><?php echo strftime('%d/%m/%Y',strtotime($comment->comment_date)); ?></div>	        </div>	        <div class="this_text_comment"><?php echo $comment->comment_content; ?></div>	</div>	<?php}$args = array('base'         => get_permalink().'%_%','format'       => '?page=%#%','total'        => $pages,'current'      => $page,'show_all'     => False,'end_size'     => 1,'mid_size'     => 2,'prev_next'    => True,'prev_text'    => __('&laquo;'),'next_text'    => __('&raquo;'),'type'         => 'plain');// ECHO THE PAGENATION ?><style type="text/css">.wp-paginate {padding: 10px 0 0 0;margin: 0 0 20px 0;font-size:10px;}.wp-paginate a {background: #ddd;border: 1px solid #ccc;color: #666 !important;margin-right: 4px;padding: 3px 6px !important;text-align: center;text-decoration: none;}.wp-paginate .current {color: #fff !important;background: #5f87ae;border: 1px solid #89adcf;margin-right: 4px;padding: 3px 6px !important;}</style><div class="wp-paginate">	<?php echo paginate_links( $args );?></div></div></div></div></div><?php get_template_part( 'sidebar', '' ); ?><?php get_footer(); ?>